<?php/* * Internet Society WordPress Template  * * The template for displaying search results. * */get_header(); ?>		<div id="container">			<div id="content" role="main">				<h1 class="page-title"><?php printf( __( 'Search Results for: %s', 'twentyten' ), '<span>' . get_search_query() . '</span>' ); ?></h1><!-- Start the Loop. --> <?php if ( have_posts() ) : ?> <ul> <?php while ( have_posts() ) : the_post(); ?> <li> <b><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a></b><br /> <?php the_excerpt(); ?> <span class="postmetadata">Posted in <?php the_category(', '); ?></span> </li> <!-- closes the first div box --> <!-- Stop The Loop (but note the "else:" - see next line). --> <?php endwhile; ?></ul><?php else: ?> <!-- The very first "if" tested to see if there were any Posts to --> <!-- display.  This "else" part tells what do if there weren't any. --> <p>Sorry, no posts matched your criteria.</p> <!-- REALLY stop The Loop. --> <?php endif; ?>   <?php include (TEMPLATEPATH . '/searchform.php'); ?>			</div><!-- #content -->		</div><!-- #container --><?php get_sidebar(); ?><?php get_footer(); ?>